package week3_program1;

public class primitivedatatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// byte - small integers
        byte b = 120;
        System.out.println("Byte value: " + b);

        // short - larger than byte
        short s = 30000;
        System.out.println("Short value: " + s);

        // int - commonly used for numbers
        int i = 100000;
        System.out.println("Int value: " + i);

        // long - very large numbers
        long l = 1234567890L; // need 'L' at the end
        System.out.println("Long value: " + l);

        // float - decimal numbers, less precision
        float f = 12.34f; // need 'f' at the end
        System.out.println("Float value: " + f);

        // double - more precision for decimals
        double d = 12345.6789;
        System.out.println("Double value: " + d);

        // char - singl character
        char c = 'A';
        System.out.println("Char value: " + c);

        // boolean - true or false
        boolean flag = true;
        System.out.println("Boolean value: " + flag);

	}

}
